// Format feedback and prepare email
const payload = $input.first();
const { from_email, message, request_type, request_type_label, timestamp, user_agent, source } = payload.body;

// Map request types to emoji and priority
const typeMap = {
  'feature_request': { emoji: '‚ú®', priority: 'HIGH', color: '#14b8a6' },
  'improvement': { emoji: 'üéØ', priority: 'HIGH', color: '#14b8a6' },
  'bug_report': { emoji: 'üêõ', priority: 'URGENT', color: '#ef4444' },
  'suggestion': { emoji: 'üí°', priority: 'MEDIUM', color: '#3b82f6' },
  'question': { emoji: '‚ùì', priority: 'LOW', color: '#8b5cf6' },
  'general': { emoji: 'üí¨', priority: 'LOW', color: '#6366f1' }
};

const typeInfo = typeMap[String(request_type)] || typeMap['general'];

// Format email subject based on type
const subjectMap = {
  'feature_request': '‚ú® New Feature Request from BIQA App',
  'improvement': 'üéØ Improvement Suggestion from BIQA App',
  'bug_report': 'üêõ BUG REPORT from BIQA App',
  'suggestion': 'üí° User Suggestion from BIQA App',
  'question': '‚ùì User Question from BIQA App',
  'general': 'üí¨ User Feedback from BIQA App'
};

const subject = subjectMap[String(request_type)] || 'BIQA Feedback';

// Format HTML email body
const htmlBody = `<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background-color: ${typeInfo.color}; color: white; padding: 20px; border-radius: 8px 8px 0 0; }
    .content { background-color: #f9fafb; padding: 20px; border: 1px solid #e5e7eb; }
    .footer { background-color: #f3f4f6; padding: 15px; border-radius: 0 0 8px 8px; font-size: 12px; color: #666; }
    .section { margin: 15px 0; }
    .label { font-weight: bold; color: ${typeInfo.color}; }
    .meta { background-color: #fff; padding: 10px; border-left: 4px solid ${typeInfo.color}; margin: 10px 0; border-radius: 4px; }
    .message-box { background-color: #ffffff; padding: 15px; border: 1px solid #e5e7eb; border-radius: 6px; margin: 15px 0; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h2 style="margin: 0;">${typeInfo.emoji} ${request_type_label}</h2>
      <p style="margin: 5px 0 0 0; opacity: 0.9;">Priority: ${typeInfo.priority}</p>
    </div>
    <div class="content">
      <div class="section">
        <p><span class="label">From:</span> ${from_email}</p>
      </div>
      <div class="meta">
        <div><span class="label">Type:</span> ${request_type_label}</div>
        <div><span class="label">Received:</span> ${new Date(timestamp).toLocaleString()}</div>
        <div><span class="label">Source:</span> BetterIslam Q&A App</div>
      </div>
      <div class="section">
        <h3 style="color: ${typeInfo.color}; margin-top: 0;">Message:</h3>
        <div class="message-box">
          ${message.replace(/\n/g, '<br>')}
        </div>
      </div>
    </div>
    <div class="footer">
      <p style="margin: 0;">
        <strong>System Info:</strong><br>
        Request Source: ${source || 'biqa_app'}<br>
        Received: ${timestamp}<br>
        Device: ${user_agent ? user_agent.substring(0, 60) + '...' : 'Unknown'}
      </p>
    </div>
  </div>
</body>
</html>`;

const plainBody = `${typeInfo.emoji} ${request_type_label}
Priority: ${typeInfo.priority}

From: ${from_email}
Type: ${request_type_label}
Received: ${new Date(timestamp).toLocaleString()}
Source: BetterIslam Q&A App

---MESSAGE---
${message}

---SYSTEM INFO---
Source: ${source || 'biqa_app'}
Timestamp: ${timestamp}`;

return {
  subject,
  htmlBody,
  plainBody,
  from_email,
  request_type,
  request_type_label,
  priority: typeInfo.priority,
  timestamp
};
